import React, { useState } from "react";
import './App.css';

function App() {
  const [count, setCount] = React.useState(0);

  const onClickDecrease = () => {
    if (count > -50) {
      setCount(prev => prev - 1);
    }
  }

  const onClickReset = () => {
    setCount(0);
  }

  const onClickIncrease = () => {
    if (count < 50) {
      setCount(prev => prev + 1);
    }
  }

  const textColor = count < 0 ? 'rgb(216, 40, 40)' : count > 0 ? 'rgb(31, 176, 31)' : 'rgb(152, 146, 146)';

  return (
    <div className="App">
      <div className="container">
        {/* <div>Press any key</div> */}
        <div className="main-content">
          <div className="button">68</div>
          <div className="name">Dasa</div>
          <div className="text">
            <p className="key"><b>Key:</b> b</p>
            <div className="divider"></div>
            <p className="code"><b>Code:</b> 83</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
