import React, { useEffect, useState } from "react";
import './App.css';

function App() {
  const [buttonPressed, setButtonPressed] = React.useState({
    key: null,
    code: null,
    number: null
  });

  useEffect(() => {
    const onClickAnyBtn = (event) => {
      console.log(`Нажата клавиша: ${event.key}`);
      console.log(event)
      setButtonPressed(`Key: ${event.key}, Code: ${event.code}`);
    }

    window.addEventListener('keydown', onClickAnyBtn);

    return () => {
      window.removeEventListener('keydown', onClickAnyBtn);
    };
  }, []);

  return (
    <div className="App">
      <div className="container">
        <div className="title">Press any key</div>
        <p>нажата клавиша: {buttonPressed}</p>
        <p>код клавиши:</p>
        {/* <div className="main-content">
          <div className="button">68</div>
          <div className="name">Dasa</div>
          <div className="text">
            <p className="key"><b>Key:</b> b</p>
            <div className="divider"></div>
            <p className="code"><b>Code:</b> 83</p>
          </div>
        </div> */}
      </div>
    </div>
  );
}

export default App;
