import React, { useEffect, useState } from "react";
import './App.css';

function App() {
  const [buttonPressed, setButtonPressed] = React.useState({
    key: null,
    code: null,
    number: null
  });

  console.log(buttonPressed)

  useEffect(() => {
    const onClickAnyBtn = (event) => {
      console.log(`Нажата клавиша: ${event.key}`);
      console.log(event);
      setButtonPressed({
        key: event.key,
        code: event.code,
        number: event.keyCode
      });
    }

    window.addEventListener('keydown', onClickAnyBtn);

    return () => {
      window.removeEventListener('keydown', onClickAnyBtn);
    };
  }, []);

  return (
    <div className="App">
      <div className="container">
        <div className="title">Press any key</div>
        {buttonPressed &&
          <>
            <p>нажата клавиша:{buttonPressed.key}</p>
            <p>код клавиши:{buttonPressed.number}</p>
          </>
        }
        {/* <div className="main-content">
          <div className="button">68</div>
          <div className="name">Dasa</div>
          <div className="text">
            <p className="key"><b>Key:</b> b</p>
            <div className="divider"></div>
            <p className="code"><b>Code:</b> 83</p>
          </div>
        </div> */}
      </div>
    </div>
  );
}

export default App;
